contracts/computeEngine/ComputeEngineImp.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "../computeEngineTeePool/ComputeEngineTeePoolProxy.sol";
import "../computeEngineTeePool/ComputeEngineTeePoolImplementation.sol";
import "../dataAccessPayment/interfaces/IPaymentRequestor.sol";
import "../dataAccessTreasury/DataAccessTreasuryBeaconProxy.sol";
import "../dataAccessTreasury/DataAccessTreasuryImplementation.sol";
import "./interfaces/ComputeEngineStorageV1.sol";

contract ComputeEngineImplementation is
    UUPSUpgradeable,
    PausableUpgradeable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable,
    ComputeEngineStorageV1
{
    using SafeERC20 for IERC20;
    using Address for address payable;

    bytes32 public constant MAINTAINER_ROLE = keccak256("MAINTAINER_ROLE");
    uint80 public constant DEDICATED_TIMEOUT = type(uint80).max;
    address public constant VANA = address(0);

    event JobRegistered(uint256 indexed jobId, address indexed ownerAddress);
    event JobCanceled(uint256 indexed jobId);
    event TeePoolCreated(
        address indexed teePoolAddress,
        IComputeEngineTeePool.TeePoolType teePoolType,
        IComputeEngineTeePool.HardwareType hardwareType,
        uint80 maxTimeout
    );
    event PaymentExecuted(uint256 indexed jobId, address indexed token, uint256 amount);
    event Deposit(address indexed account, address indexed token, uint256 amount);
    event Withdraw(address indexed account, address indexed token, uint256 amount);

    error NotJobOwner();
    error JobAlreadyDoneOrCanceled();
    error NotTee();
    error TeeAlreadyAssigned(uint256 jobId);
    error FailedToAssignTee(uint256 jobId);
    error NotLongRunningJob();
    error TeePoolNotFound();
    error TeePoolAlreadyCreated();
    error TeeNotFound();
    error InvalidTimeout();
    error NotQueryEngine();
    error UnauthorizedPaymentRequestor();
    error InsufficientBalance();
    error InvalidVanaAmount();
    error InvalidAmount();
    error JobNotFound(uint256 jobId);
    error InstructionNotFound(uint256 computeInstructionId);
    error InvalidTeePoolParams();

    modifier onlyQueryEngine() {
        if (msg.sender != queryEngine) {
            revert NotQueryEngine();
        }
        _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @notice Initializes the contract
     *
     * @param ownerAddress Address of the owner
     */
    function initialize(
        address ownerAddress,
        uint80 _ephemeralTimeout,
        uint80 _persistentTimeout,
        address _queryEngine,
        DataAccessTreasuryFactoryBeacon _dataAccessTreasuryFactory
    ) external initializer {
        __UUPSUpgradeable_init();
        __Pausable_init();
        __AccessControl_init();
        __ReentrancyGuard_init();

        ephemeralTimeout = _ephemeralTimeout;
        persistentTimeout = _persistentTimeout;
        queryEngine = _queryEngine;

        /// @dev Deploy a TeePoolFactoryBeacon
        teePoolFactory = new ComputeEngineTeePoolFactoryBeacon(
            address(new ComputeEngineTeePoolImplementation()),
            ownerAddress
        );

        /// @dev Deploy a new data access treasury for the query engine via beacon proxy
        address proxy = _dataAccessTreasuryFactory.createBeaconProxy(
            abi.encodeCall(
                DataAccessTreasuryImplementation(payable(_dataAccessTreasuryFactory.implementation())).initialize,
                (ownerAddress, address(this))
            )
        );
        computeEngineTreasury = IDataAccessTreasury(proxy);

        _setRoleAdmin(MAINTAINER_ROLE, DEFAULT_ADMIN_ROLE);
        _grantRole(DEFAULT_ADMIN_ROLE, ownerAddress);
        _grantRole(MAINTAINER_ROLE, ownerAddress);
    }

    /**
     * @notice Upgrade the contract
     * This function is required by OpenZeppelin's UUPSUpgradeable
     *
     * @param newImplementation                  new implementation
     */
    function _authorizeUpgrade(address newImplementation) internal virtual override onlyRole(DEFAULT_ADMIN_ROLE) {}

    /// @inheritdoc IComputeEngine
    function version() external pure virtual override returns (uint256) {
        return 1;
    }

    /// @inheritdoc IComputeEngine
    function pause() external override onlyRole(MAINTAINER_ROLE) {
        _pause();
    }

    /// @inheritdoc IComputeEngine
    function unpause() external override onlyRole(MAINTAINER_ROLE) {
        _unpause();
    }

    function updateQueryEngine(address queryEngineAddress) external override onlyRole(MAINTAINER_ROLE) {
        queryEngine = queryEngineAddress;
    }

    function updateComputeEngineTreasury(
        IDataAccessTreasury _computeEngineTreasuryAddress
    ) external override onlyRole(MAINTAINER_ROLE) {
        computeEngineTreasury = _computeEngineTreasuryAddress;
    }

    function updateTeePoolFactory(
        ComputeEngineTeePoolFactoryBeacon _teePoolFactoryAddress
    ) external override onlyRole(MAINTAINER_ROLE) {
        teePoolFactory = _teePoolFactoryAddress;
    }

    function updateInstructionRegistry(
        IComputeInstructionRegistry _instructionRegistry
    ) external override onlyRole(MAINTAINER_ROLE) {
        instructionRegistry = _instructionRegistry;
    }

    ////////////////////////
    ///// Job Registry /////
    ////////////////////////

    /// @inheritdoc IComputeEngine
    function jobs(uint256 jobId) external view override returns (Job memory) {
        return _jobs[jobId];
    }

    /// @inheritdoc IComputeEngine
    function submitJob(
        uint80 maxTimeout,
        bool gpuRequired,
        uint256 computeInstructionId
    ) external payable override whenNotPaused {
        uint256 jobId = _registerJob(maxTimeout, gpuRequired, computeInstructionId);

        address teeAddress = _assignJobToTee(jobId);
        if (teeAddress == address(0)) {
            /// @dev The job will be in the Registered state and can be resubmitted later or canceled
            return;
        }
        Job storage job = _jobs[jobId];
        job.teeAddress = teeAddress;
        job.status = JobStatus.Submitted;
    }

    function resubmitJob(uint256 jobId) external override whenNotPaused {
        _resubmitJobWithTee(jobId, address(0));
    }

    function resubmitJobWithTee(uint256 jobId, address teeAddress) external override whenNotPaused {
        _resubmitJobWithTee(jobId, teeAddress);
    }

    function _resubmitJobWithTee(uint256 jobId, address teeAddress) internal whenNotPaused {
        Job storage job = _jobs[jobId];
        if (job.teeAddress != address(0)) {
            revert TeeAlreadyAssigned(jobId);
        }

        if (job.status != JobStatus.Registered) {
            revert JobAlreadyDoneOrCanceled();
        }

        if (teeAddress != address(0) && job.maxTimeout <= persistentTimeout) {
            revert NotLongRunningJob();
        }

        if (teeAddress != address(0)) {
            job.teeAddress = teeAddress;
        }
        address assignedTeeAddress = _assignJobToTee(jobId);
        if (assignedTeeAddress == address(0) || teeAddress != address(0) && assignedTeeAddress != teeAddress) {
            revert FailedToAssignTee(jobId);
        }

        job.teeAddress = assignedTeeAddress;
        job.status = JobStatus.Submitted;
    }

    function _getTeePoolAddress(uint256 jobId) internal view returns (IComputeEngineTeePool) {
        Job storage job = _jobs[jobId];

        bool gpuRequired = job.gpuRequired;
        uint80 maxTimeout = job.maxTimeout;

        IComputeEngineTeePool.HardwareType hardwareType = gpuRequired
            ? IComputeEngineTeePool.HardwareType.GPU
            : IComputeEngineTeePool.HardwareType.Standard;

        IComputeEngineTeePool.TeePoolType teePoolType = _teePoolType(uint80(maxTimeout));

        return teePools(teePoolType, hardwareType);
    }

    function _assignJobToTee(uint256 jobId) internal returns (address) {
        IComputeEngineTeePool teePoolAddress = _getTeePoolAddress(jobId);
        if (address(teePoolAddress) == address(0)) {
            /**
             * @dev The job will be in the Registered state and can be resubmitted later
             * when a TEE pool is available, or the user can cancel the job.
             */
            return address(0);
        }

        Job storage job = _jobs[jobId];
        bytes memory jobParams = abi.encode(jobId, job.maxTimeout, job.gpuRequired, job.teeAddress);
        try teePoolAddress.submitJob(jobParams) returns (address teeAddress) {
            return teeAddress;
        } catch {}
        return address(0);
    }

    /// @inheritdoc IComputeEngine
    function submitJobWithTee(
        uint256 maxTimeout,
        bool gpuRequired,
        uint256 computeInstructionId,
        address teeAddress
    ) external payable override whenNotPaused {
        /// @dev Check if the job is long-running
        if (maxTimeout <= persistentTimeout) {
            revert NotLongRunningJob();
        }

        uint256 jobId = _registerJob(maxTimeout, gpuRequired, computeInstructionId);
        Job storage job = _jobs[jobId];

        /// @dev Assign the job to the Tee and submit it to the TeePool
        job.teeAddress = teeAddress;
        address assignedTeeAddress = _assignJobToTee(jobId);
        if (assignedTeeAddress != teeAddress) {
            revert FailedToAssignTee(jobId);
        }

        job.status = JobStatus.Submitted;
    }

    /// @inheritdoc IComputeEngine
    function updateJobStatus(uint256 jobId, JobStatus status, string calldata statusMessage) external override {
        Job storage job = _jobs[jobId];
        /// @dev Only the Tee assigned to the job can update the status
        if (msg.sender != job.teeAddress) {
            revert NotTee();
        }
        job.status = status;
        job.statusMessage = statusMessage;
    }

    /// @inheritdoc IComputeEngine
    function cancelJob(uint256 jobId) external override whenNotPaused {
        Job storage job = _jobs[jobId];

        if (job.ownerAddress != msg.sender) {
            revert NotJobOwner();
        }

        JobStatus status = job.status;

        if (status >= JobStatus.Completed) {
            revert JobAlreadyDoneOrCanceled();
        }

        if (status == JobStatus.Submitted || status == JobStatus.Running) {
            IComputeEngineTeePool teePool = _getTeePoolAddress(jobId);
            if (address(teePool) != address(0)) {
                teePool.removeJob(jobId);
            }
        }
        job.status = JobStatus.Canceled;

        emit JobCanceled(jobId);
    }

    /// @notice Registers a job
    /// @param maxTimeout The maximum timeout for the job
    /// @param gpuRequired True if the job requires GPU, false otherwise
    /// @param computeInstructionId The ID of the compute instruction
    /// @return jobId The ID of the job
    function _registerJob(
        uint256 maxTimeout,
        bool gpuRequired,
        uint256 computeInstructionId
    ) internal whenNotPaused returns (uint256 jobId) {
        /// @dev Deposit the job fee
        if (msg.value > 0) {
            _deposit(msg.sender, VANA, msg.value);
        }

        IComputeInstructionRegistry.ComputeInstructionInfo memory computeInstruction = instructionRegistry.instructions(
            computeInstructionId
        );
        if (computeInstruction.owner == address(0)) {
            revert InstructionNotFound(computeInstructionId);
        }

        jobId = ++jobsCount;
        Job storage job = _jobs[jobId];
        job.ownerAddress = msg.sender;
        job.maxTimeout = uint80(maxTimeout);
        job.gpuRequired = gpuRequired;
        job.status = JobStatus.Registered;
        job.computeInstructionId = uint32(computeInstructionId);
        job.addedTimestamp = uint48(block.timestamp);

        emit JobRegistered(jobId, msg.sender);
    }

    ///////////////////////////
    ///// TeePool Factory /////
    ///////////////////////////

    /// @inheritdoc IComputeEngine
    function createTeePool(
        IComputeEngineTeePool.TeePoolType teePoolType,
        IComputeEngineTeePool.HardwareType hardwareType
    ) external override onlyRole(MAINTAINER_ROLE) returns (address) {
        if (
            teePoolType == IComputeEngineTeePool.TeePoolType.None ||
            hardwareType == IComputeEngineTeePool.HardwareType.None
        ) {
            revert InvalidTeePoolParams();
        }

        bytes32 teePoolTypeId = _typePoolTypeId(teePoolType, hardwareType);
        if (address(_teePools[teePoolTypeId]) != address(0)) {
            revert TeePoolAlreadyCreated();
        }

        uint80 maxTimeout = _maxTimeout(teePoolType);

        address teePoolImpl = teePoolFactory.implementation();
        address teePoolProxy = teePoolFactory.createBeaconProxy(
            abi.encodeCall(
                ComputeEngineTeePoolImplementation(teePoolImpl).initialize,
                (msg.sender, address(this), teePoolType, hardwareType, maxTimeout)
            )
        );

        _teePools[teePoolTypeId] = IComputeEngineTeePool(teePoolProxy);

        emit TeePoolCreated(teePoolProxy, teePoolType, hardwareType, maxTimeout);

        return teePoolProxy;
    }

    function teePools(
        IComputeEngineTeePool.TeePoolType teePoolType,
        IComputeEngineTeePool.HardwareType hardwareType
    ) public view returns (IComputeEngineTeePool) {
        return _teePools[_typePoolTypeId(teePoolType, hardwareType)];
    }

    function _typePoolTypeId(
        IComputeEngineTeePool.TeePoolType teePoolType,
        IComputeEngineTeePool.HardwareType hardwareType
    ) internal pure returns (bytes32 teePoolTypeId) {
        return keccak256(abi.encodePacked(teePoolType, hardwareType));
    }

    function _maxTimeout(IComputeEngineTeePool.TeePoolType teePoolType) internal view returns (uint80 maxTimeout) {
        maxTimeout = teePoolType == IComputeEngineTeePool.TeePoolType.Ephemeral
            ? ephemeralTimeout
            : (teePoolType == IComputeEngineTeePool.TeePoolType.Persistent ? persistentTimeout : DEDICATED_TIMEOUT);
    }

    function _teePoolType(uint80 maxTimeout) internal view returns (IComputeEngineTeePool.TeePoolType teePoolType) {
        teePoolType = maxTimeout <= ephemeralTimeout
            ? IComputeEngineTeePool.TeePoolType.Ephemeral
            : (
                maxTimeout <= persistentTimeout
                    ? IComputeEngineTeePool.TeePoolType.Persistent
                    : IComputeEngineTeePool.TeePoolType.Dedicated
            );
    }

    function updateEphemeralTimeout(uint80 timeout) external override onlyRole(MAINTAINER_ROLE) {
        if (timeout >= persistentTimeout || timeout == 0) {
            revert InvalidTimeout();
        }
        ephemeralTimeout = timeout;
        IComputeEngineTeePool ephemeralStandardTeePool = teePools(
            IComputeEngineTeePool.TeePoolType.Ephemeral,
            IComputeEngineTeePool.HardwareType.Standard
        );
        if (address(ephemeralStandardTeePool) != address(0)) {
            ephemeralStandardTeePool.updateMaxTimeout(timeout);
        }
        IComputeEngineTeePool ephemeralGPUTeePool = teePools(
            IComputeEngineTeePool.TeePoolType.Ephemeral,
            IComputeEngineTeePool.HardwareType.GPU
        );
        if (address(ephemeralGPUTeePool) != address(0)) {
            ephemeralGPUTeePool.updateMaxTimeout(timeout);
        }
    }

    function updatePersistentTimeout(uint80 timeout) external override onlyRole(MAINTAINER_ROLE) {
        if (timeout <= ephemeralTimeout || timeout == DEDICATED_TIMEOUT) {
            revert InvalidTimeout();
        }
        persistentTimeout = timeout;
        IComputeEngineTeePool persistentStandardTeePool = teePools(
            IComputeEngineTeePool.TeePoolType.Persistent,
            IComputeEngineTeePool.HardwareType.Standard
        );
        if (address(persistentStandardTeePool) != address(0)) {
            persistentStandardTeePool.updateMaxTimeout(timeout);
        }
        IComputeEngineTeePool persistentGPUTeePool = teePools(
            IComputeEngineTeePool.TeePoolType.Persistent,
            IComputeEngineTeePool.HardwareType.GPU
        );
        if (address(persistentGPUTeePool) != address(0)) {
            persistentGPUTeePool.updateMaxTimeout(timeout);
        }
    }

    ///////////////////
    ///// Payment /////
    ///////////////////

    function deposit(address token, uint256 amount) external payable override whenNotPaused nonReentrant {
        _deposit(msg.sender, token, amount);
    }

    function _deposit(address from, address token, uint256 amount) internal {
        if (amount == 0) {
            revert InvalidAmount();
        }

        if (token == VANA) {
            if (msg.value != amount) {
                revert InvalidVanaAmount();
            }
            payable(address(computeEngineTreasury)).sendValue(amount);
        } else {
            IERC20(token).safeTransferFrom(from, address(this), amount);
            IERC20(token).safeTransfer(address(computeEngineTreasury), amount);
        }

        _accountBalances[from][token] += amount;

        emit Deposit(from, token, amount);
    }

    function withdraw(address token, uint256 amount) external override whenNotPaused nonReentrant {
        if (amount == 0) {
            revert InvalidAmount();
        }

        if (_accountBalances[msg.sender][token] < amount) {
            revert InsufficientBalance();
        }

        unchecked {
            _accountBalances[msg.sender][token] -= amount;
        }

        computeEngineTreasury.transfer(msg.sender, token, amount);

        emit Withdraw(msg.sender, token, amount);
    }

    function balanceOf(address account, address token) external view override returns (uint256) {
        return _accountBalances[account][token];
    }

    function executePaymentRequest(
        address token,
        uint256 amount,
        bytes calldata metadata
    ) external override onlyQueryEngine whenNotPaused nonReentrant {
        if (msg.sender == queryEngine) {
            _executePaymentRequestFromQueryEngine(token, amount, metadata);
        } else {
            revert UnauthorizedPaymentRequestor();
        }
    }

    function _executePaymentRequestFromQueryEngine(address token, uint256 amount, bytes calldata metadata) internal {
        uint256 jobId = abi.decode(metadata, (uint256));

        /// @dev Don't check job status as compute engine may delay the update of the job status
        address jobOwner = _jobs[jobId].ownerAddress;
        if (jobOwner == address(0)) {
            revert JobNotFound(jobId);
        }

        if (_accountBalances[jobOwner][token] < amount) {
            revert InsufficientBalance();
        }
        unchecked {
            _accountBalances[jobOwner][token] -= amount;
        }

        PaymentInfo storage paymentInfo = _jobPayments[jobId][token];
        paymentInfo.paidAmounts[token] += amount;
        paymentInfo.payer = jobOwner;

        emit PaymentExecuted(jobId, token, amount);

        computeEngineTreasury.transfer(msg.sender, token, amount);
    }
}

contracts/computeEngine/interfaces/Compu.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "./IComputeEngine.sol";

/**`
 * @title Storage for ComputeEngine
 * @notice For future upgrades, do not change ComputeEngineStorageV1.
 * Create a new contract which implements ComputeEngineStorageV1
 */
abstract contract ComputeEngineStorageV1 is IComputeEngine {
    uint256 public override jobsCount;
    IComputeInstructionRegistry public override instructionRegistry;
    mapping(uint256 jobId => Job job) internal _jobs;

    address public override queryEngine;
    IDataAccessTreasury public override computeEngineTreasury;
    mapping(uint256 jobId => mapping(address provider => PaymentInfo paymentInfo)) internal _jobPayments;
    mapping(address account => mapping(address token => uint256 balance)) internal _accountBalances;

    uint80 public override ephemeralTimeout;
    uint80 public override persistentTimeout;
    ComputeEngineTeePoolFactoryBeacon public override teePoolFactory;
    mapping(bytes32 teePoolTypeId => IComputeEngineTeePool teePool) internal _teePools;
}

contracts/computeEngine/interfaces/IComp.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "../../computeEngineTeePool/interfaces/IComputeEngineTeePool.sol";
import "../../dataAccessPayment/interfaces/IPaymentExecutor.sol";
import "../../dataAccessTreasury/interfaces/IDataAccessTreasury.sol";
import "../../computeInstructionRegistry/interfaces/IComputeInstructionRegistry.sol";
import "../../computeEngineTeePool/ComputeEngineTeePoolBeaconProxy.sol";

interface IComputeEngine is IPaymentExecutor {
    enum JobStatus {
        None,
        Registered, // The job is registered to the ComputeEngine contract
        Submitted, // The job is submitted to the appropriate TeePool contract and assigned to a Tee
        Running, // The job is running on the assigned Tee (the status is updated by the Tee)
        Completed,
        Failed,
        Canceled
    }

    struct Job {
        address ownerAddress;
        uint80 maxTimeout;
        bool gpuRequired;
        JobStatus status;
        address teeAddress;
        uint32 computeInstructionId;
        uint48 addedTimestamp;
        string statusMessage;
    }

    struct PaymentInfo {
        address payer;
        mapping(address token => uint256 amount) paidAmounts;
    }

    /// @notice Returns the version of the contract
    /// @return The version of the contract
    function version() external pure returns (uint256);

    /// @notice Pauses the contract
    function pause() external;

    /// @notice Unpauses the contract
    function unpause() external;

    /// @notice Returns the query engine
    /// @return The query engine
    function queryEngine() external view returns (address);

    /// @notice Updates the query engine
    /// @param queryEngineAddress The new query engine
    function updateQueryEngine(address queryEngineAddress) external;

    /// @notice Returns the compute engine treasury
    /// @return The compute engine treasury
    function computeEngineTreasury() external view returns (IDataAccessTreasury);

    /// @notice Updates the compute engine treasury
    /// @param computeEngineTreasuryAddress The new compute engine treasury
    function updateComputeEngineTreasury(IDataAccessTreasury computeEngineTreasuryAddress) external;

    function teePoolFactory() external view returns (ComputeEngineTeePoolFactoryBeacon);

    function updateTeePoolFactory(ComputeEngineTeePoolFactoryBeacon teePoolFactoryAddress) external;

    function instructionRegistry() external view returns (IComputeInstructionRegistry);

    function updateInstructionRegistry(IComputeInstructionRegistry instructionRegistryAddress) external;

    ////////////////////////
    ///// Job Registry /////
    ////////////////////////

    /// @notice Returns the number of jobs
    function jobsCount() external view returns (uint256);

    /// @notice Returns the job with the given ID
    /// @param jobId The ID of the job
    function jobs(uint256 jobId) external view returns (Job memory);

    /// @notice Submits a job to the ComputeEngine
    function submitJob(uint80 maxTimeout, bool gpuRequired, uint256 computeInstructionId) external payable;

    /// @notice Resubmits a job
    function resubmitJob(uint256 jobId) external;

    /// @notice Submits a job to the ComputeEngine and assigns it to a dedicated Tee
    /// @param maxTimeout The maximum timeout for the job
    /// @param gpuRequired True if the job requires GPU, false otherwise
    /// @param computeInstructionId The ID of the compute instruction
    /// @param teeAddress The address of the Tee to assign the job to
    function submitJobWithTee(
        uint256 maxTimeout,
        bool gpuRequired,
        uint256 computeInstructionId,
        address teeAddress
    ) external payable;

    /// @notice Resubmits a job with a dedicated Tee
    function resubmitJobWithTee(uint256 jobId, address teeAddress) external;

    /// @notice Cancels a job
    /// @param jobId The ID of the job to cancel
    function cancelJob(uint256 jobId) external;

    /// @notice Updates the status of a job
    /// @param jobId The ID of the job
    /// @param status The new status of the job
    /// @param statusMessage The status message
    /// @dev Only the Tee assigned to the job can update the status
    function updateJobStatus(uint256 jobId, JobStatus status, string calldata statusMessage) external;

    ///////////////////////////
    ///// TeePool Factory /////
    ///////////////////////////
    function ephemeralTimeout() external view returns (uint80);

    function persistentTimeout() external view returns (uint80);

    function updateEphemeralTimeout(uint80 timeout) external;

    function updatePersistentTimeout(uint80 timeout) external;

    function createTeePool(
        IComputeEngineTeePool.TeePoolType teePoolType,
        IComputeEngineTeePool.HardwareType hardwareType
    ) external returns (address);

    //////////////////////////
    ///// Wallet Balance /////
    //////////////////////////
    function deposit(address token, uint256 amount) external payable;

    function withdraw(address token, uint256 amount) external;

    function balanceOf(address account, address token) external view returns (uint256);
}

contracts/computeEngineTeePool/ComputeEn.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "hardhat/console.sol";

import {BeaconProxy} from "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import {UpgradeableBeacon} from "@openzeppelin/contracts/proxy/beacon/UpgradeableBeacon.sol";

contract ComputeEngineTeePoolFactoryBeacon is UpgradeableBeacon {
    bytes32 public constant SALT = keccak256("ComputeEngineTeePool");

    constructor(address _implementation, address _owner) UpgradeableBeacon(_implementation, _owner) {}

    function createBeaconProxy(bytes memory data) external returns (address) {
        BeaconProxy proxy = new BeaconProxy{salt: SALT}(address(this), data);
        return address(proxy);
    }

    function getProxyAddress(bytes memory data) public view returns (address) {
        bytes memory initCode = abi.encodePacked(type(BeaconProxy).creationCode, abi.encode(address(this), data));
        bytes32 bytecodeHash = keccak256(initCode);
        bytes32 hash = keccak256(abi.encodePacked(bytes1(0xff), address(this), SALT, bytecodeHash));
        return address(uint160(uint256(hash)));
    }
}

// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "./interfaces/ComputeEngineTeePoolStorageV1.sol";

contract ComputeEngineTeePoolImplementation is
    UUPSUpgradeable,
    PausableUpgradeable,
    AccessControlUpgradeable,
    ComputeEngineTeePoolStorageV1
{
    using EnumerableSet for EnumerableSet.AddressSet;
    using EnumerableSet for EnumerableSet.UintSet;

    event TeePoolTypeUpdated(TeePoolType newTeePoolType);
    event HardwareTypeUpdated(HardwareType newHardwareType);
    event TeeAdded(address indexed teeAddress, string url, string publicKey);
    event TeeRemoved(address indexed teeAddress);
    event JobSubmitted(uint256 indexed jobId, address teeAddress);
    event JobRemoved(uint256 indexed jobId);

    error TeeAlreadyAdded();
    error TeeNotActive();
    error NoActiveTee();
    error NotComputeEngine();
    error HWRequirementNotMet();
    error MaxTimeoutExceeded();

    bytes32 public constant MAINTAINER_ROLE = keccak256("MAINTAINER_ROLE");

    modifier onlyComputeEngine() {
        if (msg.sender != computeEngine) {
            revert NotComputeEngine();
        }
        _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @notice Initializes the contract
     *
     * @param ownerAddress Address of the owner
     */
    function initialize(
        address ownerAddress,
        address computeEngineAddress,
        TeePoolType _teePoolType,
        HardwareType _hardwareType,
        uint80 _maxTimeout
    ) external initializer {
        __UUPSUpgradeable_init();
        __Pausable_init();
        __AccessControl_init();

        computeEngine = computeEngineAddress;
        teePoolType = _teePoolType;
        hardwareType = _hardwareType;
        maxTimeout = _maxTimeout;

        _setRoleAdmin(MAINTAINER_ROLE, DEFAULT_ADMIN_ROLE);
        _grantRole(DEFAULT_ADMIN_ROLE, ownerAddress);
        _grantRole(MAINTAINER_ROLE, ownerAddress);
    }

    /**
     * @notice Upgrade the contract
     * This function is required by OpenZeppelin's UUPSUpgradeable
     *
     * @param newImplementation                  new implementation
     */
    function _authorizeUpgrade(address newImplementation) internal virtual override onlyRole(DEFAULT_ADMIN_ROLE) {}

    /// @inheritdoc IComputeEngineTeePool
    function updateComputeEngine(address newComputeEngine) external override onlyRole(MAINTAINER_ROLE) {
        computeEngine = newComputeEngine;
    }

    /// @inheritdoc ITeePool
    function version() external pure virtual override returns (uint256) {
        return 1;
    }

    /// @inheritdoc ITeePool
    function pause() external override onlyRole(MAINTAINER_ROLE) {
        _pause();
    }

    /// @inheritdoc ITeePool
    function unpause() external override onlyRole(MAINTAINER_ROLE) {
        _unpause();
    }

    /// @inheritdoc IComputeEngineTeePool
    function updateTeePoolType(TeePoolType newTeePoolType) external override onlyComputeEngine {
        teePoolType = newTeePoolType;
        emit TeePoolTypeUpdated(newTeePoolType);
    }

    /// @inheritdoc IComputeEngineTeePool
    function updateHardwareType(HardwareType newHardwareType) external override onlyComputeEngine {
        hardwareType = newHardwareType;
        emit HardwareTypeUpdated(newHardwareType);
    }

    /// @inheritdoc IComputeEngineTeePool
    function updateMaxTimeout(uint80 newMaxTimeout) external override onlyComputeEngine {
        maxTimeout = newMaxTimeout;
    }

    /// @inheritdoc ITeePool
    function teeList() external view override returns (address[] memory) {
        return _teeList.values();
    }

    /// @inheritdoc ITeePool
    function activeTeeList() external view override returns (address[] memory) {
        return _activeTeeList.values();
    }

    /// @inheritdoc ITeePool
    function teesCount() external view override returns (uint256) {
        return _teeList.length();
    }

    /// @inheritdoc ITeePool
    function activeTeesCount() external view override returns (uint256) {
        return _activeTeeList.length();
    }

    /// @inheritdoc ITeePool
    function isTee(address teeAddress) public view override returns (bool) {
        return _tees[teeAddress].status == TeeStatus.Active;
    }

    /// @inheritdoc IComputeEngineTeePool
    function tees(address teeAddress) public view override returns (TeeInfo memory) {
        Tee storage tee = _tees[teeAddress];
        return
            TeeInfo({
                teeAddress: teeAddress,
                url: tee.url,
                status: tee.status,
                jobsCount: tee.jobIdsList.length(),
                publicKey: tee.publicKey
            });
    }

    /// @inheritdoc IComputeEngineTeePool
    function teeListAt(uint256 index) external view override returns (TeeInfo memory) {
        return tees(_teeList.at(index));
    }

    /// @inheritdoc IComputeEngineTeePool
    function activeTeeListAt(uint256 index) external view override returns (TeeInfo memory) {
        return tees(_activeTeeList.at(index));
    }

    /// @inheritdoc ITeePool
    function addTee(address teeAddress, bytes calldata params) external override onlyRole(MAINTAINER_ROLE) {
        if (_activeTeeList.contains(teeAddress)) {
            revert TeeAlreadyAdded();
        }

        _teeList.add(teeAddress);
        _activeTeeList.add(teeAddress);

        (string memory url, string memory publicKey) = abi.decode(params, (string, string));
        Tee storage tee = _tees[teeAddress];
        tee.url = url;
        tee.status = TeeStatus.Active;
        tee.publicKey = publicKey;
        emit TeeAdded(teeAddress, url, publicKey);
    }

    /// @inheritdoc ITeePool
    function removeTee(address teeAddress) external override onlyRole(MAINTAINER_ROLE) {
        if (!_activeTeeList.contains(teeAddress)) {
            revert TeeNotActive();
        }

        _tees[teeAddress].status = TeeStatus.Removed;
        _activeTeeList.remove(teeAddress);

        emit TeeRemoved(teeAddress);
    }

    /// @inheritdoc ITeePool
    /// @dev Only the ComputeEngine contract can submit jobs to its TeePool.
    function submitJob(bytes calldata params) external override onlyComputeEngine whenNotPaused returns (address) {
        if (_activeTeeList.length() == 0) {
            revert NoActiveTee();
        }

        /// @dev Decode the parameters
        (uint256 jobId, uint80 jobMaxTimeout, bool gpuRequired, address assignedTee) = abi.decode(
            params,
            (uint256, uint80, bool, address)
        );

        if (gpuRequired && hardwareType != HardwareType.GPU) {
            revert HWRequirementNotMet();
        }

        if (jobMaxTimeout > maxTimeout) {
            revert MaxTimeoutExceeded();
        }

        address teeAddress;
        if (teePoolType != TeePoolType.Dedicated) {
            /// @dev The job is assigned to a Tee in a round-robin fashion
            teeAddress = tees(_activeTeeList.at(_jobsCount % _activeTeeList.length())).teeAddress;
        } else {
            if (!isTee(assignedTee)) {
                revert TeeNotActive();
            }
            teeAddress = assignedTee;
        }

        _tees[teeAddress].jobIdsList.add(jobId);
        _jobTee[jobId] = teeAddress;

        /// @dev _jobsCount is an internal job counter of the pool
        /// JobId is assigned by the ComputeEngine contract, via
        /// a universal job counter, accross multiple TeePools.
        _jobsCount += 1;

        emit JobSubmitted(jobId, teeAddress);

        return teeAddress;
    }

    /// @inheritdoc ITeePool
    /// @dev Only the ComputeEngine contract can remove jobs from their TeePool.
    function removeJob(uint256 jobId) external override onlyComputeEngine whenNotPaused {
        address teeAddress = _jobTee[jobId];
        if (teeAddress == address(0)) {
            return;
        }
        _jobTee[jobId] = address(0);
        _tees[teeAddress].jobIdsList.remove(jobId);
        emit JobRemoved(jobId);
    }
}

