contracts/computeEngineTeePool/interface.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "./IComputeEngineTeePool.sol";

/**
 * @title Storage for ComputeEngineTeePool
 * @notice For future upgrades, do not change ComputeEngineTeePoolStorageV1.
 * Create a new contract which implements ComputeEngineTeePoolStorageV1.
 */
abstract contract ComputeEngineTeePoolStorageV1 is IComputeEngineTeePool {
    /// @dev Packed storage layout to save on storage slots
    TeePoolType public override teePoolType; // 1 byte
    HardwareType public override hardwareType; // 1 byte
    uint80 public override maxTimeout; // 10 bytes
    address public override computeEngine; // 20 bytes

    EnumerableSet.AddressSet internal _teeList;
    EnumerableSet.AddressSet internal _activeTeeList;
    mapping(address teeAddress => Tee tee) internal _tees;

    uint256 internal _jobsCount;
    /// @dev Jobs are managed by the ComputeEngine contract, which is the job registry.
    /// The TeePool contract only keeps track of the Tee assigned to each job.
    mapping(uint256 => address) internal _jobTee;
}

// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "./ITeePool.sol";

interface IComputeEngineTeePool is ITeePool {
    enum TeePoolType {
        None,
        Ephemeral,
        Persistent,
        Dedicated
    }

    enum HardwareType {
        None,
        Standard, // CPU only
        GPU
    }

    enum TeeStatus {
        None,
        Active,
        Removed
    }

    struct Tee {
        string url;
        TeeStatus status;
        EnumerableSet.UintSet jobIdsList;
        string publicKey;
    }

    struct TeeInfo {
        address teeAddress;
        string url;
        TeeStatus status;
        uint256 jobsCount;
        string publicKey;
    }

    /// @notice Returns the type of the Tee pool
    function teePoolType() external view returns (TeePoolType);

    /// @notice Updates the type of the Tee pool
    /// @param teePoolType The new type of the Tee pool
    function updateTeePoolType(TeePoolType teePoolType) external;

    /// @notice Returns the hardware type of the Tee pool
    function hardwareType() external view returns (HardwareType);

    /// @notice Updates the hardware type of the Tee pool
    function updateHardwareType(HardwareType hardwareType) external;

    /// @notice Returns the maximum timeout for a job
    function maxTimeout() external view returns (uint80);

    /// @notice Updates the maximum timeout for a job
    /// @param maxTimeout The new maximum timeout for a job
    function updateMaxTimeout(uint80 maxTimeout) external;

    /// @notice Returns the ComputeEngine contract address
    function computeEngine() external view returns (address);

    /// @notice Updates the ComputeEngine contract address
    /// @param computeEngineAddress The new ComputeEngine contract address
    function updateComputeEngine(address computeEngineAddress) external;

    /// @notice Returns the Tee info for the given address
    /// @param teeAddress The address of the Tee
    /// @return The Tee info
    function tees(address teeAddress) external view returns (TeeInfo memory);

    /// @notice Returns the Tee info for the Tee at the given index
    /// @param index The index of the Tee
    /// @return The Tee info
    function teeListAt(uint256 index) external view returns (TeeInfo memory);

    /// @notice Returns the Tee info for the active Tee at the given index
    /// @param index The index of the active Tee
    /// @return The Tee info
    function activeTeeListAt(uint256 index) external view returns (TeeInfo memory);
}

// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

/// @title A generic interface for a TeePool contract
/// @notice A contract or interface that inherits this interface should
/// define its own TeeInfo and Job structs.
interface ITeePool {
    /// @notice Returns the version of the contract
    /// @return The version of the contract
    function version() external pure returns (uint256);

    /// @notice Pauses the contract
    function pause() external;

    /// @notice Unpauses the contract
    function unpause() external;

    /// @notice Returns the number of Tees
    /// @return The number of Tees
    function teesCount() external view returns (uint256);

    /// @notice Returns the list of Tee addresses
    /// @return The list of Tee addresses
    function teeList() external view returns (address[] memory);

    /// @notice Returns the number of active Tees
    /// @return The number of active Tees
    function activeTeesCount() external view returns (uint256);

    /// @notice Returns the list of active Tee addresses
    /// @return The list of active Tee addresses
    function activeTeeList() external view returns (address[] memory);

    /// @notice Checks if the given address is a Tee
    /// @param teeAddress The address to check
    /// @return True if the address is a Tee, false otherwise
    function isTee(address teeAddress) external view returns (bool);

    /// @notice Adds a Tee to the pool
    /// @param teeAddress The address of the Tee
    /// @param params The parameters for the Tee
    function addTee(address teeAddress, bytes calldata params) external;

    /// @notice Removes a Tee from the pool
    /// @param teeAddress The address of the Tee
    function removeTee(address teeAddress) external;

    /// @notice Submits a job to the pool
    /// @param params The parameters for the job
    /// @return The address of the Tee assigned to the job
    function submitJob(bytes calldata params) external returns (address);

    /// @notice Removes a job in the pool
    /// @param jobId The ID of the job to remove
    function removeJob(uint256 jobId) external;
}

