// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "./interfaces/ComputeInstructionRegistryStorageV1.sol";

contract ComputeInstructionRegistryImplementation is
    UUPSUpgradeable,
    PausableUpgradeable,
    AccessControlUpgradeable,
    ComputeInstructionRegistryStorageV1
{
    bytes32 public constant MAINTAINER_ROLE = keccak256("MAINTAINER_ROLE");

    event ComputeInstructionAdded(
        uint256 indexed computeInstructionId,
        address indexed owner,
        string computeInstructionUrl,
        bytes32 computeInstructionHash
    );

    error NotRefinerOwner();

    /// @notice Reverts if the caller is not the owner of the refiner
    /// @param refinerId The ID of the refiner
    modifier onlyRefinerOwner(uint256 refinerId) {
        if (refinerRegistry.refiners(refinerId).owner != msg.sender) {
            revert NotRefinerOwner();
        }
        _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @notice Initializes the contract
     *
     * @param ownerAddress Address of the owner
     */
    function initialize(address ownerAddress, address refinerRegistryAddress) external initializer {
        __UUPSUpgradeable_init();
        __Pausable_init();
        __AccessControl_init();

        refinerRegistry = IDataRefinerRegistry(refinerRegistryAddress);

        _setRoleAdmin(MAINTAINER_ROLE, DEFAULT_ADMIN_ROLE);
        _grantRole(DEFAULT_ADMIN_ROLE, ownerAddress);
        _grantRole(MAINTAINER_ROLE, ownerAddress);
    }

    /**
     * @notice Upgrades the contract
     * This function is required by OpenZeppelin's UUPSUpgradeable
     *
     * @param newImplementation New implementation
     */
    function _authorizeUpgrade(address newImplementation) internal virtual override onlyRole(DEFAULT_ADMIN_ROLE) {}

    function updateRefinerRegistry(address refinerRegistryAddress) external onlyRole(MAINTAINER_ROLE) {
        refinerRegistry = IDataRefinerRegistry(refinerRegistryAddress);
    }

    /// @inheritdoc IComputeInstructionRegistry
    function version() external pure virtual override returns (uint256) {
        return 1;
    }

    /// @inheritdoc IComputeInstructionRegistry
    function pause() external override onlyRole(MAINTAINER_ROLE) {
        _pause();
    }

    /// @inheritdoc IComputeInstructionRegistry
    function unpause() external override onlyRole(MAINTAINER_ROLE) {
        _unpause();
    }

    /// @inheritdoc IComputeInstructionRegistry
    function instructions(uint256 instructionId) external view returns (ComputeInstructionInfo memory) {
        ComputeInstruction storage instruction = _instructions[instructionId];
        return ComputeInstructionInfo(instruction.hash, instruction.owner, instruction.url);
    }

    /// @inheritdoc IComputeInstructionRegistry
    function isApproved(uint256 instructionId, uint256 refinerId) external view returns (bool) {
        return _instructions[instructionId].dlpApprovals[refinerId];
    }

    /// @inheritdoc IComputeInstructionRegistry
    function addComputeInstruction(bytes32 hash, string calldata url) external returns (uint256 computeInstructionId) {
        computeInstructionId = ++instructionsCount;

        ComputeInstruction storage instruction = _instructions[computeInstructionId];
        instruction.hash = hash;
        instruction.owner = msg.sender;
        instruction.url = url;

        emit ComputeInstructionAdded(computeInstructionId, msg.sender, url, hash);
    }

    /// @inheritdoc IComputeInstructionRegistry
    function updateComputeInstruction(
        uint256 instructionId,
        uint256 refinerId,
        bool approved
    ) external onlyRefinerOwner(refinerId) {
        _instructions[instructionId].dlpApprovals[refinerId] = approved;
    }
}

