        if (epochsCount == 0) {
            revert NothingToClaim();
        }

        for (uint256 i = 0; i < stakeIds.length; i++) {
            _claimStakeRewardUntilEpoch(stakeIds[i], epochsCount - 1);
        }
    }

    /**
     * @notice Claims rewards for a stake up to specified epoch
     */
    function claimStakeRewardUntilEpoch(uint256 stakeId, uint256 lastEpochToClaim) external nonReentrant whenNotPaused {
        _createEpochsUntilBlockNumber(block.number);
        if (epochsCount == 0) {
            revert InvalidParam();
        }
        uint256 maxEpoch = Math.min(lastEpochToClaim, epochsCount - 1);
        _claimStakeRewardUntilEpoch(stakeId, maxEpoch);
    }

    /**
     * @notice Calculates stake score based on amount and duration
     */
    function calculateStakeScore(
        uint256 stakeAmount,
        uint256 stakeStartBlock,
        uint256 blockNumber
    ) public view returns (uint256) {
        uint256 daysStaked = (blockNumber - stakeStartBlock) / daySize;
        return (stakeAmount * _getMultiplier(daysStaked)) / 100;
    }

    /**
     * @notice Creates a new stake for a DLP
     * @dev Validates stake amount and DLP status before creating
     */
    function _createStake(address stakerAddress, uint256 dlpId, uint256 amount) internal {
        if (stakerAddress == address(0)) {
            revert InvalidAddress();
        }

        if (amount < minStakeAmount) {
            revert InvalidStakeAmount();
        }

        Dlp storage dlp = _dlps[dlpId];

        if (
            dlp.status != DlpStatus.Registered &&
            dlp.status != DlpStatus.Eligible &&
            dlp.status != DlpStatus.SubEligible
        ) {
            revert InvalidDlpStatus();
        }

        Stake storage stake = _stakes[++stakesCount];
        stake.amount = amount;
        stake.startBlock = block.number;
        stake.stakerAddress = stakerAddress;
        stake.dlpId = dlpId;
        stake.lastClaimedIndexEpochId = dlp.epochIdsCount;

        Staker storage staker = _stakers[stakerAddress];
        staker.dlpIds.add(dlpId);
        staker.dlpStakeAmounts[dlpId] += amount;
        staker.stakeIds.add(stakesCount);
        staker.totalStakeAmount += amount;

        _stakersList.add(stakerAddress);
        _checkpointAdd(dlp.stakeAmountCheckpoints, amount);

        // Check if DLP becomes eligible
        if (
            dlp.status != DlpStatus.Eligible &&
            _dlpComputedStakeAmount(dlpId) >= dlpEligibilityThreshold &&
            _eligibleDlpsList.length() < eligibleDlpsLimit
        ) {
            _eligibleDlpsList.add(dlpId);
            dlp.status = DlpStatus.Eligible;
            emit DlpBecameEligible(dlpId);
        }

        emit StakeCreated(stakesCount, stakerAddress, dlpId, amount);
    }

    /**
     * @notice Closes a stake and updates DLP status if needed
     */
    function _closeStake(address stakerAddress, uint256 stakeId) internal {
        Stake storage stake = _stakes[stakeId];

        if (stake.stakerAddress != stakerAddress) {
            revert NotStakeOwner();
        }

        if (stake.endBlock != 0) {
            revert StakeAlreadyClosed();
        }

        Staker storage staker = _stakers[stakerAddress];
        staker.dlpStakeAmounts[stake.dlpId] -= stake.amount;
        staker.totalStakeAmount -= stake.amount;

        Dlp storage dlp = _dlps[stake.dlpId];
        _checkpointAdd(dlp.unstakeAmountCheckpoints, stake.amount);
        stake.endBlock = block.number;

        uint256 dlpStake = _dlpComputedStakeAmount(stake.dlpId);

        // Update DLP status based on remaining stake
        if (
            dlpStake < dlpSubEligibilityThreshold &&
            (dlp.status == DlpStatus.SubEligible || dlp.status == DlpStatus.Eligible)
        ) {
            dlp.status = DlpStatus.Registered;
            _eligibleDlpsList.remove(stake.dlpId);
        } else if (dlpStake < dlpEligibilityThreshold && dlp.status == DlpStatus.Eligible) {
            dlp.status = DlpStatus.SubEligible;
        }

        emit StakeClosed(stakeId);
    }

    /**
     * @notice Withdraws a closed stake after delay period
     */
    function _withdrawStake(address stakerAddress, uint256 stakeId) internal {
        Stake storage stake = _stakes[stakeId];

        if (stake.stakerAddress != stakerAddress) {
            revert NotStakeOwner();
        }

        if (stake.withdrawn) {
            revert StakeAlreadyWithdrawn();
        }

        if (stake.endBlock == 0) {
            revert StakeNotClosed();
        }

        if (stake.endBlock + stakeWithdrawalDelay() > block.number) {
            revert StakeWithdrawalTooEarly();
        }

        stake.withdrawn = true;

        (bool success, ) = stakerAddress.call{value: stake.amount}("");
        if (!success) {
            revert TransferFailed();
        }

        emit StakeWithdrawn(stakeId);
    }

    /**
     * @notice Internal function to register a new DLP
     */
    function _registerDlp(DlpRegistration calldata registrationInfo) internal {
        if (registrationInfo.ownerAddress == address(0) || registrationInfo.treasuryAddress == address(0)) {
            revert InvalidAddress();
        }

        if (dlpIds[registrationInfo.dlpAddress] != 0) {
            revert InvalidDlpStatus();
        }

        if (dlpNameToId[registrationInfo.name] != 0 || bytes(registrationInfo.name).length == 0) {
            revert InvalidName();
        }

        if (
            registrationInfo.stakersPercentage < minDlpStakersPercentage ||
            registrationInfo.stakersPercentage > maxDlpStakersPercentage
        ) {
            revert InvalidStakersPercentage();
        }

        if (msg.value < minDlpRegistrationStake) {
            revert InvalidStakeAmount();
        }

        uint256 dlpId = ++dlpsCount;
        Dlp storage dlp = _dlps[dlpId];

        dlp.id = dlpId;
        dlp.dlpAddress = registrationInfo.dlpAddress;
        dlp.ownerAddress = registrationInfo.ownerAddress;
        dlp.treasuryAddress = registrationInfo.treasuryAddress;
        _checkpointPush(dlp.stakersPercentageCheckpoints, registrationInfo.stakersPercentage);
        dlp.name = registrationInfo.name;
        dlp.iconUrl = registrationInfo.iconUrl;
        dlp.website = registrationInfo.website;
        dlp.metadata = registrationInfo.metadata;
        dlp.status = DlpStatus.Registered;
        dlp.registrationBlockNumber = block.number;

        dlpIds[registrationInfo.dlpAddress] = dlpId;

        dlpNameToId[registrationInfo.name] = dlpId;

        _createStake(registrationInfo.ownerAddress, dlpId, msg.value);

        emit DlpRegistered(
            dlpId,
            registrationInfo.dlpAddress,
            registrationInfo.ownerAddress,
            registrationInfo.treasuryAddress,
            registrationInfo.stakersPercentage,
            registrationInfo.name,
            registrationInfo.iconUrl,
            registrationInfo.website,
            registrationInfo.metadata
        );
    }

    /**
     * @notice Claims reward for a stake up to specified epoch
     * @dev Calculates and distributes rewards based on stake score
     */
    function _claimStakeRewardUntilEpoch(uint256 stakeId, uint256 lastEpochToClaim) internal {
        uint256 totalRewardAmount = _calculateStakeRewardUntilEpoch(stakeId, lastEpochToClaim, true);

        if (totalRewardAmount == 0) {
            revert NothingToClaim();
        }

        Stake storage stake = _stakes[stakeId];

        (bool success, ) = payable(stake.stakerAddress).call{value: totalRewardAmount}("");
        if (!success) {
            revert TransferFailed();
        }
    }

    /**
     * @notice Calculates reward for a stake up to specified epoch
     */
    function _calculateStakeRewardUntilEpoch(
        uint256 stakeId,
        uint256 lastEpochToClaim,
        bool isClaim
    ) internal returns (uint256) {
        Stake storage stake = _stakes[stakeId];
        Dlp storage dlp = _dlps[stake.dlpId];

        uint256 totalRewardAmount;
        uint256 epochToClaimIndex = stake.lastClaimedIndexEpochId + 1;
        uint256 epochToClaim = dlp.epochIds[epochToClaimIndex];
        uint256 rewardClaimDelayTmp = rewardClaimDelay();

        while (epochToClaim > 0 && epochToClaim <= lastEpochToClaim) {
            Epoch storage epoch = _epochs[epochToClaim];
            EpochDlp storage epochDlp = epoch.dlps[stake.dlpId];

            if (
                epochToClaim == 0 ||
                epochDlp.totalStakesScore == 0 ||
                (stake.endBlock > 0 && epoch.endBlock > stake.endBlock)
            ) {
                break;
            }

            uint256 stakeScore = calculateStakeScore(stake.amount, stake.startBlock, epoch.endBlock);

            uint256 rewardAmount = (((epochDlp.rewardAmount * epochDlp.stakersPercentage) / 100e18) * stakeScore) /
                epochDlp.totalStakesScore;

            uint256 numberOfDays = 1 + (block.number - epoch.endBlock) / daySize;

            if (rewardClaimDelayTmp > 0 && numberOfDays < rewardClaimDelayTmp) {
                rewardAmount = (rewardAmount * numberOfDays) / rewardClaimDelayTmp;
            } else if (isClaim) {
                stake.lastClaimedIndexEpochId = epochToClaimIndex;
            }

            if (stake.claimedAmounts[epochToClaim] >= rewardAmount) {
                break;
            }

            totalRewardAmount += rewardAmount - stake.claimedAmounts[epochToClaim];

            if (isClaim) {
                stake.claimedAmounts[epochToClaim] = rewardAmount;
            }

            epochToClaim = dlp.epochIds[++epochToClaimIndex];
        }

        return totalRewardAmount;
    }

    /**
     * @notice Returns stake score multiplier based on duration
     */
    function _getMultiplier(uint256 index) internal pure returns (uint256) {
        if (index >= 64) {
            return 300;
        }

        uint16[64] memory multiplier = [
            100,
            102,
            105,
            107,
            110,
            112,
            114,
            117,
            119,
            121,
            124,
            126,
            129,
            131,
            133,
            136,
            138,
            140,
            143,
            145,
            148,
            150,
            156,
            162,
            168,
            174,
            180,
            186,
            192,
            198,
            204,
            210,
            215,
            221,
            227,
            233,
            239,
            245,
            251,
            257,
            263,
            269,
            275,
            276,
            277,
            279,
            280,
            281,
            282,
            283,
            285,
            286,
            287,
            288,
            289,
            290,
            292,
            293,
            294,
            295,
            296,
            298,
            299,
            300
        ];
        return uint256(multiplier[index]);
    }

    /**
     * @notice Helper function to add value to checkpoint
     */
    function _checkpointAdd(Checkpoints.Trace208 storage store, uint256 delta) private returns (uint208, uint208) {
        return store.push(SafeCast.toUint48(block.number), store.latest() + SafeCast.toUint208(delta));
    }

    /**
     * @notice Helper function to set checkpoint value
     */
    function _checkpointPush(Checkpoints.Trace208 storage store, uint256 delta) private returns (uint208, uint208) {
        return store.push(SafeCast.toUint48(block.number), SafeCast.toUint208(delta));
    }

    /**
     * @notice Get DLP stake amount at specific block
     */
    function _dlpComputedStakeAmountByBlock(uint256 dlpId, uint48 checkBlock) internal view returns (uint256) {
        return
            _dlps[dlpId].stakeAmountCheckpoints.upperLookup(checkBlock) -
            _dlps[dlpId].unstakeAmountCheckpoints.upperLookup(checkBlock);
    }

    /**
     * @notice Get current DLP stake amount
     */
    function _dlpComputedStakeAmount(uint256 dlpId) internal view returns (uint256) {
        return _dlps[dlpId].stakeAmountCheckpoints.latest() - _dlps[dlpId].unstakeAmountCheckpoints.latest();
    }

    /**
     * @notice Creates and finalizes epochs up to target block
     */
    function _createEpochsUntilBlockNumber(uint256 blockNumber) internal {
        Epoch storage lastEpoch = _epochs[epochsCount];

        if (lastEpoch.endBlock > block.number) {
            return;
        }

        while (lastEpoch.endBlock < blockNumber) {
            lastEpoch.isFinalised = true;

            _finalizeEpoch(epochsCount);

            Epoch storage newEpoch = _epochs[++epochsCount];
            newEpoch.startBlock = lastEpoch.endBlock + 1;
            newEpoch.endBlock = newEpoch.startBlock + epochSize - 1;
            newEpoch.rewardAmount = epochRewardAmount;

            emit EpochCreated(epochsCount);
            lastEpoch = newEpoch;
        }
    }

    /**
     * @notice Finalizes epoch by distributing rewards to top DLPs
     */
    function _finalizeEpoch(uint256 epochId) internal {
        if (epochId == 0) {
            return;
        }
        Epoch storage epoch = _epochs[epochId];
        uint256[] memory topDlps = topDlpIds(epochDlpsLimit);

        uint256 index;
        uint256 topDlpsTotalStakeAmount;
        uint256 dlpId;
        EpochDlp storage epochDlp;
        Dlp storage dlp;

        // Calculate total stake amount
        for (index = 0; index < topDlps.length; index++) {
            topDlpsTotalStakeAmount += _dlpComputedStakeAmount(topDlps[index]);
        }

        // Distribute rewards
        for (index = 0; index < topDlps.length; index++) {
            dlpId = topDlps[index];
            epoch.dlpIds.add(dlpId);
            dlp = _dlps[dlpId];

            dlp.epochIds[++dlp.epochIdsCount] = epochId;

            epochDlp = epoch.dlps[dlpId];

            if (dlp.registrationBlockNumber > epoch.startBlock) {
                epochDlp.stakersPercentage = dlp.stakersPercentageCheckpoints.at(0)._value;
            } else {
                epochDlp.stakersPercentage = dlp.stakersPercentageCheckpoints.upperLookup(
                    SafeCast.toUint48(epoch.startBlock)
                );
            }
            epochDlp.rewardAmount = (_dlpComputedStakeAmount(dlpId) * epoch.rewardAmount) / topDlpsTotalStakeAmount;

            // Send treasury portion of rewards
            if (epochDlp.stakersPercentage < 100e18) {
                (bool success, ) = dlp.treasuryAddress.call{
                    value: (epochDlp.rewardAmount * (100e18 - epochDlp.stakersPercentage)) / 100e18
                }("");

                if (success) {
                    epochDlp.rewardClaimed = true;
                } else {
                    //just skip this DLP; it will be fixed manually
                }
            }
        }
    }
}

contracts/root/interfaces/DLPRootStorage.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "./IDLPRoot.sol";

/**
 * @notice For future upgrades, do not change DLPRootStorageV1. Create a new
 * contract which implements DLPRootStorageV1
 */
abstract contract DLPRootStorageV1 is IDLPRoot {
    address internal _trustedForwarder;
    uint256 public override eligibleDlpsLimit; // Must be below 500 for gas efficiency
    uint256 public override epochDlpsLimit; // Max DLPs per epoch
    uint256 public override minStakeAmount; // Minimum stake allowed
    uint256 public override minDlpStakersPercentage; // Min % of rewards to stakers (in 1e18)
    uint256 public override minDlpRegistrationStake; // Min stake for new DLP registration
    uint256 public override dlpEligibilityThreshold; // Min stake for full eligibility
    uint256 public override dlpSubEligibilityThreshold; // Min stake for sub-eligibility

    // Historical values tracked using checkpoints
    Checkpoints.Trace208 internal _stakeWithdrawalDelayCheckpoints;
    Checkpoints.Trace208 internal _rewardClaimDelayCheckpoints;

    uint256 public override epochRewardAmount; // Rewards per epoch
    uint256 public override epochSize; // Blocks per epoch
    uint256 public override daySize; // Blocks per day

    // DLP management
    uint256 public override dlpsCount;
    mapping(uint256 dlpId => Dlp dlp) internal _dlps;
    mapping(address dlpAddress => uint256 dlpId) public override dlpIds;
    EnumerableSet.UintSet internal _eligibleDlpsList;

    // Epoch tracking
    uint256 public override epochsCount;
    mapping(uint256 epochId => Epoch epoch) internal _epochs;

    // Staker management
    EnumerableSet.AddressSet internal _stakersList;
    mapping(address stakerAddress => Staker staker) internal _stakers;

    // Stake tracking
    uint256 public override stakesCount;
    mapping(uint256 stakeId => Stake stake) internal _stakes;

    uint256 public override maxDlpStakersPercentage; // Max % of rewards to stakers (in 1e18)

    mapping(string dlpName => uint256 dlpId) public override dlpNameToId;
}

contracts/root/interfaces/IDLPRoot.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/utils/structs/Checkpoints.sol";

interface IDLPRoot {
    // DLP lifecycle states from registration to deregistration
    enum DlpStatus {
        None,
        Registered,
        Eligible, // Can participate in epochs
        SubEligible, // Below threshold but above minimum
        Deregistered
    }

    struct Dlp {
        uint256 id;
        address dlpAddress;
        address ownerAddress;
        address payable treasuryAddress; // Receives non-staker rewards
        Checkpoints.Trace208 stakersPercentageCheckpoints; // Historical staker percentages
        string name;
        string iconUrl;
        string website;
        string metadata;
        DlpStatus status;
        uint256 registrationBlockNumber;
        Checkpoints.Trace208 stakeAmountCheckpoints; // Historical stake amounts
        Checkpoints.Trace208 unstakeAmountCheckpoints; // Historical unstake amounts
        uint256 epochIdsCount; // Number of participated epochs
        mapping(uint256 index => uint256 epochIds) epochIds;
    }

    struct EpochDlp {
        uint256 rewardAmount; // Rewards allocated to this DLP
        uint256 stakersPercentage; // % going to stakers vs treasury
        uint256 totalStakesScore; // Sum of weighted stake scores
        bool rewardClaimed; // True if reward has been claimed
    }

    struct Epoch {
        uint256 startBlock;
        uint256 endBlock;
        uint256 rewardAmount;
        bool isFinalised;
        EnumerableSet.UintSet dlpIds; // Participating DLPs
        mapping(uint256 dlpId => EpochDlp epochDlp) dlps;
    }

    struct Stake {
        address stakerAddress;
        uint256 dlpId;
        uint256 amount;
        uint256 startBlock;
        uint256 endBlock; // 0 if active
        bool withdrawn;
        uint256 lastClaimedIndexEpochId;
        mapping(uint256 epochId => uint256 claimedAmount) claimedAmounts;
    }

    struct Staker {
        EnumerableSet.UintSet dlpIds; // DLPs staked on by this staker
        mapping(uint256 dlpId => uint256 dlpStakeAmount) dlpStakeAmounts;
        EnumerableSet.UintSet stakeIds; // Stakes made by this staker
        uint256 totalStakeAmount;
    }

    // View functions for contract state and configuration
    function version() external pure returns (uint256);
    function epochDlpsLimit() external view returns (uint256);
    function eligibleDlpsLimit() external view returns (uint256);
    function epochSize() external view returns (uint256);
    function daySize() external view returns (uint256);
    function eligibleDlpsListValues() external view returns (uint256[] memory);
    function eligibleDlpsListCount() external view returns (uint256);
    function eligibleDlpsListAt(uint256 index) external view returns (uint256);
    function epochsCount() external view returns (uint256);

    // Read-only struct views
    struct EpochInfo {
        uint256 startBlock;
        uint256 endBlock;
        uint256 reward;
        bool isFinalised;
        uint256[] dlpIds;
    }
    function epochs(uint256 epochId) external view returns (EpochInfo memory);

    // Additional view functions
    function minStakeAmount() external view returns (uint256);
    function minDlpStakersPercentage() external view returns (uint256);
    function maxDlpStakersPercentage() external view returns (uint256);
    function minDlpRegistrationStake() external view returns (uint256);
    function dlpEligibilityThreshold() external view returns (uint256);
    function dlpSubEligibilityThreshold() external view returns (uint256);
    function stakeWithdrawalDelay() external view returns (uint256);
    function rewardClaimDelay() external view returns (uint256);
    function epochRewardAmount() external view returns (uint256);
    function dlpsCount() external view returns (uint256);

    struct DlpInfo {
        uint256 id;
        address dlpAddress;
        address ownerAddress;
        address treasuryAddress;
        uint256 stakersPercentage;
        uint256 stakersPercentageEpoch;
        string name;
        string iconUrl;
        string website;
        string metadata;
        DlpStatus status;
        uint256 registrationBlockNumber;
        uint256 stakeAmount;
        uint256[] epochIds;
    }
    function dlps(uint256 index) external view returns (DlpInfo memory);
    function dlpsByAddress(address dlpAddress) external view returns (DlpInfo memory);
    function dlpIds(address dlpAddress) external view returns (uint256);
    function dlpNameToId(string calldata dlpName) external view returns (uint256);
    function dlpsByName(string calldata dlpName) external view returns (DlpInfo memory);

    struct DlpEpochInfo {
        uint256 stakeAmount; // 0 if not a top DLP
        bool isTopDlp; // In top DLPs list this epoch
        uint256 rewardAmount; // 0 if not top DLP or epoch not finished
        uint256 stakersPercentage; // 0 if not top DLP
        uint256 totalStakesScore; // 0 if not top DLP
        bool rewardClaimed;
    }
    function dlpEpochs(uint256 dlpId, uint256 epochId) external view returns (DlpEpochInfo memory);
    function stakersListCount() external view returns (uint256);
    function stakersListAt(uint256 index) external view returns (address);
    function stakerDlpsListCount(address stakerAddress) external view returns (uint256);
    function stakerDlpsListAt(address stakerAddress, uint256 index) external view returns (uint256);
    function stakerDlpsListValues(address stakerAddress) external view returns (uint256[] memory);
    function stakerStakesListCount(address stakerAddress) external view returns (uint256);
    function stakerStakesListAt(address stakerAddress, uint256 index) external view returns (uint256);
    function stakerStakesListValues(address stakerAddress) external view returns (uint256[] memory);
    function stakerTotalStakeAmount(address stakerAddress) external view returns (uint256);
    function stakerDlpStakeAmount(address stakerAddress, uint256 dlpId) external view returns (uint256);
    function stakesCount() external view returns (uint256);

    struct StakeInfo {
        uint256 id;
        address stakerAddress;
        uint256 dlpId;
        uint256 amount;
        uint256 startBlock;
        uint256 endBlock;
        bool withdrawn;
        uint256 lastClaimedEpochId;
    }
    function stakes(uint256 stakeId) external view returns (StakeInfo memory);

    // Core functionality
    function topDlpIds(uint256 numberOfDlps) external returns (uint256[] memory);
    function calculateStakeClaimableAmount(uint256 stakeId) external returns (uint256);

    struct DlpRewardApy {
        uint256 dlpId;
        uint256 APY; //annual percentage yield
        uint256 EPY; //epoch percentage yield
    }

    function estimatedDlpRewardPercentages(uint256[] memory dlpIds) external view returns (DlpRewardApy[] memory);

    // Admin functions
    function pause() external;
    function unpause() external;
    function updateEpochDlpsLimit(uint256 newEpochDlpsLimit) external;
    function updateEligibleDlpsLimit(uint256 newEligibleDlpsLimit) external;
    function updateEpochSize(uint256 newEpochSize) external;
    function updateEpochRewardAmount(uint256 newEpochRewardAmount) external;
    function updateMinStakeAmount(uint256 newMinStakeAmount) external;
    function updateMinDlpStakersPercentage(uint256 newMinDlpStakersPercentage) external;
    function updateMaxDlpStakersPercentage(uint256 newMaxDlpStakersPercentage) external;
    function updateMinDlpRegistrationStake(uint256 newMinStakeAmount) external;
    function updateDlpEligibilityThreshold(uint256 newDlpEligibilityThreshold) external;
    function updateDlpSubEligibilityThreshold(uint256 newDlpSubEligibilityThreshold) external;
    function updateStakeWithdrawalDelay(uint256 newStakeWithdrawalDelay) external;
    function updateRewardClaimDelay(uint256 newSRewardClaimDelay) external;

    struct EpochDlpsTotalStakesScore {
        uint256 epochId;
        uint256 dlpId;
        uint256 totalStakesScore;
    }
    function saveEpochDlpsTotalStakesScore(EpochDlpsTotalStakesScore[] memory stakeScore) external;
    function overrideEpochDlpsTotalStakesScore(EpochDlpsTotalStakesScore memory stakeScore) external;

    // Epoch management
    function createEpochs() external;
    function createEpochsUntilBlockNumber(uint256 blockNumber) external;
    function overrideEpoch(uint256 epochId, uint256 startBlock, uint256 endBlock, uint256 rewardAmount) external;

    struct DlpRegistration {
        address dlpAddress;
        address ownerAddress;
        address payable treasuryAddress;
        uint256 stakersPercentage;
        string name;
        string iconUrl;
        string website;
        string metadata;
    }

    // DLP lifecycle management
    function registerDlp(DlpRegistration calldata registrationInfo) external payable;
    function updateDlp(uint256 dlpId, DlpRegistration calldata dlpUpdateInfo) external;
    function deregisterDlp(uint256 dlpId) external;

    // Staking and rewards
    function claimStakeRewardUntilEpoch(uint256 stakeId, uint256 lastEpochToClaim) external;
    function claimStakesReward(uint256[] memory stakeIds) external;
    function createStake(uint256 dlpId) external payable;
    function closeStakes(uint256[] memory stakeIds) external;
    function withdrawStakes(uint256[] memory stakeIds) external;
}
