contracts/vanaPool/interfaces/IVanaPool..content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import {IVanaPoolEntity} from "../../vanaPoolEntity/interfaces/IVanaPoolEntity.sol";
import {IVanaPoolTreasury} from "../../vanaPoolTreasury/interfaces/IVanaPoolTreasury.sol";

interface IVanaPool {
    struct StakerEntity {
        uint256 shares;
    }

    struct Staker {
        mapping(uint256 entityId => StakerEntity entity) entities;
    }

    function version() external pure returns (uint256);
    function vanaPoolEntity() external view returns (IVanaPoolEntity);
    function vanaPoolTreasury() external view returns (IVanaPoolTreasury);
    function minStakeAmount() external view returns (uint256);

    function stake(uint256 entityId) external payable;
    function unstake(uint256 entityId, uint256 amount) external;
    function getStakerEntityShares(address staker, uint256 entityId) external view returns (uint256);

    function pause() external;
    function unpause() external;
    function updateVanaPoolEntity(address newVanaPoolEntityAddress) external;
    function updateVanaPoolTreasury(address newVanaPoolTreasuryAddress) external;
    function updateMinStakeAmount(uint256 newMinStake) external;

    function sharesToVana(uint256 entityId, uint256 sharesAmount) external view returns (uint256);
    function vanaToShares(uint256 entityId, uint256 vanaAmount) external view returns (uint256);

    function registerEntityStake(uint256 entityId, address ownerAddress, uint256 registrationStake) external;
}

contracts/vanaPool/interfaces/VanaPoolSt.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "./IVanaPool.sol";

/**
 * @title Storage for VanaPool
 * @notice For future upgrades, do not change VanaPoolStorageV1. Create a new
 * contract which implements VanaPoolStorageV1
 */
abstract contract VanaPoolStorageV1 is IVanaPool {
    address internal _trustedForwarder;

    IVanaPoolEntity public override vanaPoolEntity;
    IVanaPoolTreasury public override vanaPoolTreasury;

    uint256 public override minStakeAmount;

    EnumerableSet.AddressSet internal _stakersList;
    mapping(address => Staker) internal _stakers;
}

contracts/vanaPool/VanaPoolImplementatio.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/metatx/ERC2771ContextUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/MulticallUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "./interfaces/VanaPoolStorageV1.sol";

contract VanaPoolImplementation is
    UUPSUpgradeable,
    PausableUpgradeable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable,
    MulticallUpgradeable,
    ERC2771ContextUpgradeable,
    VanaPoolStorageV1
{
    using EnumerableSet for EnumerableSet.AddressSet;

    bytes32 public constant MAINTAINER_ROLE = keccak256("MAINTAINER_ROLE");
    bytes32 public constant VANA_POOL_ENTITY = keccak256("VANA_POOL_ENTITY");

    /**
     * @notice Triggered when a user stakes VANA to an entity
     *
     * @param entityId                         ID of the entity
     * @param staker                           address of the staker
     * @param amount                           amount staked
     * @param sharesIssued                     shares issued
     */
    event Staked(uint256 indexed entityId, address indexed staker, uint256 amount, uint256 sharesIssued);

    /**
     * @notice Triggered when a user unstakes VANA from an entity
     *
     * @param entityId                         ID of the entity
     * @param staker                           address of the staker
     * @param amount                           amount unstaked
     * @param sharesBurned                     shares burned
     */
    event Unstaked(uint256 indexed entityId, address indexed staker, uint256 amount, uint256 sharesBurned);

    /**
     * @notice Triggered when minimum stake amount is updated
     *
     * @param newMinStake                      new minimum stake amount
     */
    event MinStakeUpdated(uint256 newMinStake);

    /**
     * @notice Triggered when VanaPoolEntity contract is updated
     *
     * @param newVanaPoolEntity                new VanaPoolEntity address
     */
    event VanaPoolEntityUpdated(address newVanaPoolEntity);

    /**
     * @notice Triggered when an entity stake is registered
     *
     * @param entityId                         ID of the entity
     * @param ownerAddress                     address of the owner
     */
    event EntityStakeRegistered(uint256 indexed entityId, address indexed ownerAddress);

    error InsufficientStakeAmount();
    error InsufficientShares();
    error TransferFailed();
    error InvalidAmount();
    error EntityNotFound();
    error EntityNotActive();
    error InvalidAddress();
    error InvalidEntity();
    error NotEntityOwner();
    error CannotRemoveRegistrationStake();
    error NotAuthorized();

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() ERC2771ContextUpgradeable(address(0)) {
        _disableInitializers();
    }

    /**
     * @notice Initialize the contract
     *
     * @param trustedForwarderAddress           address of the trusted forwarder
     * @param ownerAddress                      address of the owner
     * @param initialMinStake                   initial minimum stake amount (in wei)
     */
    function initialize(
        address trustedForwarderAddress,
        address ownerAddress,
        uint256 initialMinStake
    ) external initializer {
        __AccessControl_init();
        __UUPSUpgradeable_init();
        __Pausable_init();
        __ReentrancyGuard_init();
        __Multicall_init();

        _trustedForwarder = trustedForwarderAddress;
        minStakeAmount = initialMinStake;

        _setRoleAdmin(MAINTAINER_ROLE, DEFAULT_ADMIN_ROLE);

        _grantRole(DEFAULT_ADMIN_ROLE, ownerAddress);
        _grantRole(MAINTAINER_ROLE, ownerAddress);

        // Note: VanaPoolEntity will be set later via updateVanaPoolEntity
    }

    /**
     * @notice Upgrade the contract
     * This function is required by OpenZeppelin's UUPSUpgradeable
     *
     * @param newImplementation                  new implementation
     */
    function _authorizeUpgrade(address newImplementation) internal virtual override onlyRole(DEFAULT_ADMIN_ROLE) {}

    function _msgSender()
        internal
        view
        override(ContextUpgradeable, ERC2771ContextUpgradeable)
        returns (address sender)
    {
        return ERC2771ContextUpgradeable._msgSender();
    }

    function _msgData() internal view override(ContextUpgradeable, ERC2771ContextUpgradeable) returns (bytes calldata) {
        return ERC2771ContextUpgradeable._msgData();
    }

    function _contextSuffixLength()
        internal
        view
        override(ContextUpgradeable, ERC2771ContextUpgradeable)
        returns (uint256)
    {
        return ERC2771ContextUpgradeable._contextSuffixLength();
    }

    function _checkRole(bytes32 role) internal view override {
        _checkRole(role, msg.sender);
    }

    function trustedForwarder() public view virtual override returns (address) {
        return _trustedForwarder;
    }

    /**
     * @notice Returns the version of the contract
     */
    function version() external pure virtual override returns (uint256) {
        return 1;
    }

    /**
     * @notice Returns staker info for a specific entity
     *
     * @param staker                           address of the staker
     * @param entityId                         ID of the entity
     * @return uint256                         shares owned by staker in the entity
     */
    function getStakerEntityShares(address staker, uint256 entityId) external view override returns (uint256) {
        return _stakers[staker].entities[entityId].shares;
    }

    /**
     * @notice Convert shares to VANA for a specific entity
     *
     * @param entityId                          ID of the entity
     * @param sharesAmount                      amount of shares
     * @return uint256                          corresponding VANA value
     */
    function sharesToVana(uint256 entityId, uint256 sharesAmount) public view override returns (uint256) {
        //todo: try to get the sharesToVana from the VanaPoolEntity contract only once per call

        IVanaPoolEntity.EntityInfo memory entityInfo = vanaPoolEntity.entities(entityId);

        if (entityInfo.totalShares == 0) {
            return 1;
        }
        return (sharesAmount * (entityInfo.totalStaked + entityInfo.activeRewardPool)) / entityInfo.totalShares;
    }

    /**
     * @notice Convert VANA to shares for a specific entity
     *
     * @param entityId                          ID of the entity
     * @param vanaAmount                        amount of VANA
     * @return uint256                          corresponding shares amount
     */
    function vanaToShares(uint256 entityId, uint256 vanaAmount) public view override returns (uint256) {
        //todo: try to get the vanaToShares from the VanaPoolEntity contract only once per call
        IVanaPoolEntity.EntityInfo memory entityInfo = vanaPoolEntity.entities(entityId);

        uint256 totalValue = entityInfo.totalStaked + entityInfo.activeRewardPool;
        if (totalValue == 0) {
            return vanaAmount;
        }
        return (vanaAmount * entityInfo.totalShares) / totalValue;
    }

    /**
     * @dev Pauses the contract
     */
    function pause() external override onlyRole(MAINTAINER_ROLE) {
        _pause();
    }

    /**
     * @dev Unpauses the contract
     */
    function unpause() external override onlyRole(MAINTAINER_ROLE) {
        _unpause();
    }

    /**
     * @notice Updates the minimum stake amount
     *
     * @param newMinStake                       new minimum stake amount in wei
     */
    function updateMinStakeAmount(uint256 newMinStake) external override onlyRole(MAINTAINER_ROLE) {
        minStakeAmount = newMinStake;
        emit MinStakeUpdated(newMinStake);
    }

    /**
     * @notice Update the trusted forwarder
     *
     * @param trustedForwarderAddress           address of the trusted forwarder
     */
    function updateTrustedForwarder(address trustedForwarderAddress) external onlyRole(MAINTAINER_ROLE) {
        _trustedForwarder = trustedForwarderAddress;
    }

    /**
     * @notice Update the VanaPoolEntity contract address
     *
     * @param newVanaPoolEntityAddress                new VanaPoolEntity contract address
     */
    function updateVanaPoolEntity(address newVanaPoolEntityAddress) external override onlyRole(MAINTAINER_ROLE) {
        if (newVanaPoolEntityAddress == address(0)) {
            revert InvalidEntity();
        }

        // Revoke and grant roles
        if (address(vanaPoolEntity) != address(0)) {
            _revokeRole(VANA_POOL_ENTITY, address(vanaPoolEntity));
        }
        _grantRole(VANA_POOL_ENTITY, newVanaPoolEntityAddress);

        vanaPoolEntity = IVanaPoolEntity(newVanaPoolEntityAddress);
        emit VanaPoolEntityUpdated(newVanaPoolEntityAddress);
    }

    /**
     * @notice Update the VanaPoolTreasury contract
     *
     * @param newVanaPoolTreasuryAddress                new VanaPoolTreasury contract address
     */
    function updateVanaPoolTreasury(address newVanaPoolTreasuryAddress) external override onlyRole(MAINTAINER_ROLE) {
        if (newVanaPoolTreasuryAddress == address(0)) {
            revert InvalidAddress();
        }

        vanaPoolTreasury = IVanaPoolTreasury(newVanaPoolTreasuryAddress);
        emit VanaPoolEntityUpdated(newVanaPoolTreasuryAddress);
    }

    /**
     * @notice Stake VANA into a specific entity
     *
     * @param entityId                          ID of the entity to stake into
     */
    function stake(uint256 entityId) external payable override nonReentrant whenNotPaused {
        // Verify the entity exists and is active in VanaPoolEntity contract
        if (!_isValidEntity(entityId)) {
            revert EntityNotActive();
        }

        if (msg.value < minStakeAmount) {
            revert InsufficientStakeAmount();
        }

        // Get entity info from VanaPoolEntity
        IVanaPoolEntity.EntityInfo memory entityInfo = vanaPoolEntity.entities(entityId);

        // Process entity rewards through VanaPoolEntity to ensure current share price is used
        vanaPoolEntity.processRewards(entityId);

        // Calculate shares
        uint256 sharesIssued = vanaToShares(entityId, msg.value);

        // Update staker's position
        _stakers[_msgSender()].entities[entityId].shares += sharesIssued;

        _stakersList.add(_msgSender());

        // Update entity staking data in VanaPoolEntity contract
        vanaPoolEntity.updateEntityStake(entityId, sharesIssued, msg.value, true);

        (bool success, ) = payable(address(vanaPoolTreasury)).call{value: msg.value}("");

        if (!success) {
            revert TransferFailed();
        }

        emit Staked(entityId, _msgSender(), msg.value, sharesIssued);
    }

    /**
     * @notice Unstake VANA from a specific entity
     *
     * @param entityId                          ID of the entity to unstake from
     * @param shareAmount                       shareAmount to unstake
     */
    function unstake(uint256 entityId, uint256 shareAmount) external override nonReentrant whenNotPaused {
        uint256 stakerShares = _stakers[_msgSender()].entities[entityId].shares;
        if (stakerShares == 0 || shareAmount == 0) {
            revert InvalidAmount();
        }

        // Get entity info from VanaPoolEntity
        IVanaPoolEntity.EntityInfo memory entityInfo = vanaPoolEntity.entities(entityId);

        // If this is the entity owner, ensure they can't unstake below the registration stake
        if (entityInfo.ownerAddress == _msgSender()) {
            uint256 ownerMinShares = vanaToShares(entityId, vanaPoolEntity.minRegistrationStake());

            if (stakerShares - shareAmount < ownerMinShares) {
                revert CannotRemoveRegistrationStake();
            }
        }

        // Process entity rewards through VanaPoolEntity to ensure current share price is used
        vanaPoolEntity.processRewards(entityId);

        // Store the exact VANA amount corresponding to shares at this point
        uint256 exactVanaAmount = sharesToVana(entityId, shareAmount);

        // Update staker's position
        _stakers[_msgSender()].entities[entityId].shares -= shareAmount;

        // Update entity staking data in VanaPoolEntity contract
        vanaPoolEntity.updateEntityStake(entityId, shareAmount, exactVanaAmount, false);

        bool success = vanaPoolTreasury.transferVana(payable(_msgSender()), exactVanaAmount);
        if (!success) {
            revert TransferFailed();
        }

        emit Unstaked(entityId, _msgSender(), exactVanaAmount, shareAmount);
    }

    /**
     * @notice Register stake for a new entity (called by VanaPoolEntity contract)
     *
     * @param entityId                          ID of the entity
     * @param ownerAddress                      Address of the entity owner
     */
    function registerEntityStake(
        uint256 entityId,
        address ownerAddress,
        uint256 registrationStake
    ) external override nonReentrant whenNotPaused onlyRole(VANA_POOL_ENTITY) {
        // Register shares for the owner
        _stakers[ownerAddress].entities[entityId].shares = registrationStake;

        _stakersList.add(ownerAddress);

        emit EntityStakeRegistered(entityId, ownerAddress);
    }

    /**
     * @notice Checks if entity exists and is active
     *
     * @param entityId                         ID of the entity
     * @return bool                            true if entity exists and is active
     */
    function _isValidEntity(uint256 entityId) internal view returns (bool) {
        // Check if entity exists in VanaPoolEntity contract
        IVanaPoolEntity.EntityInfo memory entityInfo = vanaPoolEntity.entities(entityId);
        return entityInfo.status == IVanaPoolEntity.EntityStatus.Active;
    }
}

