// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.24;

import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/Ownable2StepUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "./interfaces/MultisendStorageV1.sol";

contract MultisendImplementation is
    UUPSUpgradeable,
    Ownable2StepUpgradeable,
    ReentrancyGuardUpgradeable,
    MultisendStorageV1
{
    error InvalidAmount();

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @notice Used to initialize a new Faucet contract
     *
     * @param ownerAddress            Address of the owner
     */
    function initialize(address ownerAddress) external initializer {
        __Ownable2Step_init();
        __UUPSUpgradeable_init();
        __ReentrancyGuard_init();

        _transferOwnership(ownerAddress);
    }

    /**
     * @notice Fallback function to receive VANA
     */
    receive() external payable {}

    /**
     * @notice Upgrade the contract
     * This function is required by OpenZeppelin's UUPSUpgradeable
     *
     * @param newImplementation                  new implementation
     */
    function _authorizeUpgrade(address newImplementation) internal virtual override onlyOwner {}

    function distribute(uint256 amount, address payable[] memory recipients) public payable nonReentrant {
        if (msg.value < amount * recipients.length) {
            revert InvalidAmount();
        }

        for (uint256 i = 0; i < recipients.length; i++) {
            if (recipients[i].balance > amount) {
                continue;
            }

            recipients[i].call{value: amount}("");
        }
    }

    /**
     * @notice Allows the owner to withdraw VANA from the contract
     *
     * @param _to       address where the VANA will be send
     * @param _amount   amount to withdraw
     */
    function withdraw(address _to, uint256 _amount) external onlyOwner nonReentrant returns (bool success) {
        (success, ) = _to.call{value: _amount}("");
        return success;
    }
}

